package com.choncheng.maya.pay;/** *  * @项目名称：Maya * @类名称：Result * @类描述： 支付宝支付返回结果实体 * @创建人：李波 * @创建时间：2015-8-28 下午1:49:22 * @版本号：v1.0 *  */public class Result {	public String resultStatus;	public String result;	public String memo;	public Result(String rawResult) {		try {			String[] resultParams = rawResult.split(";");			for (String resultParam : resultParams) {				if (resultParam.startsWith("resultStatus")) {					resultStatus = gatValue(resultParam, "resultStatus");				}				if (resultParam.startsWith("result")) {					result = gatValue(resultParam, "result");				}				if (resultParam.startsWith("memo")) {					memo = gatValue(resultParam, "memo");				}			}		} catch (Exception e) {			e.printStackTrace();		}	}	@Override	public String toString() {		return "resultStatus={" + resultStatus + "};memo={" + memo				+ "};result={" + result + "}";	}	private String gatValue(String content, String key) {		String prefix = key + "={";		return content.substring(content.indexOf(prefix) + prefix.length(),				content.lastIndexOf("}"));	}}